            })
        })
            .then(response => response.json())
            .then(data => console.log(data))
    }, [currentTicket])

    const handleScanBtn = () => {
        setCamOn((prev) => !prev);
    }

    // <video ref={ref} />
    return (
        <div className="ScanPage">
            <div>
                {camOff? 
                <video ref={ref} />
                :
                <h1>stats</h1>
                }
            </div>
            <Link to='/stats'>
                <button className="themed-btn">Stats</button>
            </Link>
            <button className="themed-btn" onClick={() => {camOff = !camOff}}>Scan</button>
        </div>
    );
}
