import React, { useState, useEffect } from 'react';
import { useZxing } from 'react-zxing';
import TicketConfirmation from '../components/TicketConfirmation';
import Link from '../components/Link';

export default function ScanPage(){
    const [camOn, setCamOn] = useState(true);
    const [manualInput, setManualInput] = useState("");
    const [currentTicket, setCurrentTicket] = useState("");
    const { ref } = useZxing({
        onDecodeResult(currentTicket) {
          setCurrentTicket(currentTicket.getText());
        },
        paused: !camOn
      });

    const handleTextChange = (event) => {
        setManualInput(event.target.value);
    }
    const handleSubmitBtn = () => {
        setCurrentTicket(manualInput);
        setManualInput("");
    }

    useEffect(() => {
        fetch('localhost', {
            method: 'POST',
            body: JSON.stringify({

            })
        })
            .then(response => response.json())
            .then(data => console.log(data))
    }, [currentTicket])

    const handleScanBtn = () => {
        setCamOn((prev) => !prev);
    }

    // <video ref={ref} />
    return (
        <div className="ScanPage">
            <div>
                {camOn? 
                <video className='scanbox' ref={ref} />
                :
                <h1>stats</h1>
                }
            </div>
            {currentTicket &&
                <TicketConfirmation name='hi' email='test' code={currentTicket} clearCurrent={setCurrentTicket("")}/>
            }
            <div>        
                <input className='ticket-id-input' value={manualInput} onChange={handleTextChange} type='text' placeholder='Ticket id (e.g. 12345678901)'></input>
                <button className="themed-btn" onClick={handleSubmitBtn}>Submit</button>
            </div>
            <Link to='/stats'>
                <button className="themed-btn">Stats</button>
            </Link>
            <button className="themed-btn" onClick={handleScanBtn}>Scan</button>
        </div>
    );
}
