import React, { useState, useEffect } from 'react';
import { useZxing } from 'react-zxing';
import TicketConfirmation from '../components/TicketConfirmation';
import Link from '../components/Link';

export default function ScanPage(){
    const [camOn, setCamOn] = useState(false);
    const [manualInput, setManualInput] = useState("");
    const [currentTicket, setCurrentTicket] = useState("");
    const [currentInfo, setCurrentInfo] = useState();
    const { ref } = useZxing({
        onDecodeResult(result) {
          setCurrentTicket(result.getText());
          setCamOn(false);
        },
        paused: !camOn
      });

    const handleTextChange = (event) => {
        setManualInput(event.target.value);
    }
    const handleSubmitBtn = () => {
        setCurrentTicket(manualInput);
        setManualInput("");
        setCamOn(false);
    }

    useEffect(() => {
        const apiLink = 'http://127.0.0.1:8080/ticketinfo/'
        if (currentTicket){
            fetch(apiLink + currentTicket, {
                method: 'GET',
            })
                .then(response => response.json())
                .then(data => setCurrentInfo(data.data))
        }
    }, [currentTicket])

    const handleScanBtn = () => {
        setCamOn((prev) => !prev);
    }

    return (
        <div className="ScanPage">
            <div>
                {camOn? 
                <video className='scanbox' ref={ref} />
                :
                <h1>stats</h1>
                }
            </div>
            {currentInfo &&
                <TicketConfirmation firstname={currentInfo.firstname} surname={currentInfo.surname} email={currentInfo.email} code={currentTicket} clearCurrent={() => {setCurrentInfo("")}}/>
            }
            <div>        
                <input className='ticket-id-input' value={manualInput} onChange={handleTextChange} type='text' placeholder='Ticket id (e.g. 12345678901)'></input>
                <button className="themed-btn" onClick={handleSubmitBtn}>Submit</button>
            </div>
            <Link to='/stats'>
                <button className="themed-btn">Stats</button>
            </Link>
            <button className="themed-btn" onClick={handleScanBtn}>{camOn? "Camera Off": "Scan"}</button>
        </div>
    );
}
